import React, { useRef, useState, useEffect } from 'react';
import * as THREE from 'three';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls, Text, Float } from '@react-three/drei';
import { colors } from '../styles/col';

// 3D Head component with expressions
function PhiloHead({ mood, isTalking }) {
    const headRef = useRef();
    const [eyePosition, setEyePosition] = useState(0);

    // Mood-based colors
    const moodColors = {
        thoughtful: colors.moods.thoughtful,
        curious: colors.moods.curious,
        playful: colors.moods.playful,
        mysterious: colors.moods.mysterious,
        confused: colors.moods.confused,
        wise: colors.moods.wise,
    };

    const currentColor = moodColors[mood] || colors.primary[500];

    // Animation for talking and mood changes
    useFrame((state) => {
        if (headRef.current) {
            // Gentle floating animation
            headRef.current.position.y = Math.sin(state.clock.elapsedTime) * 0.05;

            // Eye movement for "thinking"
            const eyeMove = Math.sin(state.clock.elapsedTime * 2) * 0.02;
            setEyePosition(eyeMove);

            // Mouth animation for talking
            if (isTalking) {
                const mouthScale = 0.8 + Math.sin(state.clock.elapsedTime * 10) * 0.2;
                headRef.current.children[3].scale.y = mouthScale;
            }
        }
    });

    return (
        <group ref={headRef}>
            {/* Head */}
            <mesh position={[0, 0, 0]}>
                <sphereGeometry args={[1, 32, 32]} />
                <meshStandardMaterial color={currentColor} roughness={0.3} metalness={0.1} />
            </mesh>

            {/* Left Eye */}
            <mesh position={[-0.3, 0.2, 0.8]}>
                <sphereGeometry args={[0.15, 16, 16]} />
                <meshStandardMaterial color="#FFFFFF" />
            </mesh>
            <mesh position={[-0.3 + eyePosition, 0.2, 0.95]}>
                <sphereGeometry args={[0.08, 16, 16]} />
                <meshStandardMaterial color="#000000" />
            </mesh>

            {/* Right Eye */}
            <mesh position={[0.3, 0.2, 0.8]}>
                <sphereGeometry args={[0.15, 16, 16]} />
                <meshStandardMaterial color="#FFFFFF" />
            </mesh>
            <mesh position={[0.3 + eyePosition, 0.2, 0.95]}>
                <sphereGeometry args={[0.08, 16, 16]} />
                <meshStandardMaterial color="#000000" />
            </mesh>

            {/* Mouth */}
            <mesh position={[0, -0.3, 0.8]} scale={[0.5, 0.3, 0.1]}>
                <boxGeometry args={[1, 1, 1]} />
                <meshStandardMaterial color="#E91E63" />
            </mesh>

            {/* Thought bubble when thinking */}
            {mood === 'thoughtful' && (
                <Float speed={2} rotationIntensity={0.5} floatIntensity={1}>
                    <mesh position={[1.5, 1, 0]}>
                        <sphereGeometry args={[0.3, 16, 16]} />
                        <meshStandardMaterial color="#FFFFFF" transparent opacity={0.8} />
                        <Text
                            position={[0, 0, 0.3]}
                            fontSize={0.15}
                            color="#000000"
                            anchorX="center"
                            anchorY="middle"
                        >
                            ðŸ¤”
                        </Text>
                    </mesh>
                </Float>
            )}

            {/* Question mark when curious */}
            {mood === 'curious' && (
                <Float speed={3} rotationIntensity={1}>
                    <Text
                        position={[1.2, 0.8, 0]}
                        fontSize={0.4}
                        color={colors.accent[500]}
                        anchorX="center"
                        anchorY="middle"
                    >
                        ?
                    </Text>
                </Float>
            )}
        </group>
    );
}

// Main 3D Avatar Component
export default function PhiloAvatar3D({
    mood = 'thoughtful',
    isTalking = false,
    onAvatarClick
}) {
    const [rotation, setRotation] = useState([0, 0, 0]);

    const moodDescriptions = {
        thoughtful: "RÃ©flexion profonde",
        curious: "CuriositÃ© intense",
        playful: "Joueur et espiÃ¨gle",
        mysterious: "MystÃ©rieux et Ã©nigmatique",
        confused: "LÃ©gÃ¨rement perdu",
        wise: "Sagesse ancienne"
    };

    return (
        <div style={styles.container}>
            <div style={styles.canvasContainer}>
                <Canvas
                    camera={{ position: [0, 0, 3], fov: 50 }}
                    style={{ background: 'transparent' }}
                >
                    <ambientLight intensity={0.5} />
                    <pointLight position={[10, 10, 10]} />
                    <directionalLight position={[5, 5, 5]} intensity={1} />

                    <PhiloHead mood={mood} isTalking={isTalking} />

                    <OrbitControls
                        enableZoom={false}
                        enablePan={false}
                        autoRotate={true}
                        autoRotateSpeed={0.5}
                        maxPolarAngle={Math.PI / 2}
                        minPolarAngle={Math.PI / 2}
                    />
                </Canvas>
            </div>

            <div style={styles.infoPanel}>
                <div style={styles.moodIndicator}>
                    <div style={{
                        ...styles.moodDot,
                        backgroundColor: colors.moods[mood] || colors.primary[500]
                    }} />
                    <span style={styles.moodText}>
                        Humeur: <strong>{moodDescriptions[mood]}</strong>
                    </span>
                </div>

                <div style={styles.stats}>
                    <div style={styles.statItem}>
                        <span style={styles.statValue}>ðŸŽ¯</span>
                        <span style={styles.statLabel}>Engagement</span>
                    </div>
                    <div style={styles.statItem}>
                        <span style={styles.statValue}>ðŸ’­</span>
                        <span style={styles.statLabel}>Profondeur</span>
                    </div>
                    <div style={styles.statItem}>
                        <span style={styles.statValue}>ðŸ˜„</span>
                        <span style={styles.statLabel}>Humeur</span>
                    </div>
                </div>

                <button
                    onClick={onAvatarClick}
                    style={styles.interactButton}
                >
                    Interagir avec Philo
                </button>
            </div>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px',
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        borderRadius: '20px',
        boxShadow: '0 10px 30px rgba(33, 150, 243, 0.15)',
        backdropFilter: 'blur(10px)',
    },
    canvasContainer: {
        width: '300px',
        height: '300px',
        borderRadius: '15px',
        overflow: 'hidden',
        marginBottom: '20px',
    },
    infoPanel: {
        width: '100%',
        textAlign: 'center',
    },
    moodIndicator: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        gap: '10px',
        marginBottom: '15px',
    },
    moodDot: {
        width: '12px',
        height: '12px',
        borderRadius: '50%',
        animation: 'pulse 2s infinite',
    },
    moodText: {
        fontSize: '14px',
        color: colors.neutral[700],
    },
    stats: {
        display: 'flex',
        justifyContent: 'space-around',
        marginBottom: '20px',
        padding: '15px',
        backgroundColor: colors.neutral[50],
        borderRadius: '12px',
    },
    statItem: {
        display: 'flex',
        flexDirection: 'column',
